    // name: () => "nicolas", //어떤 사용자가 name query를 보내면 'nicolas'를 반환하는 함수로 답할 것.
    people: () => people,
    person: (_, { id }) => getById(id), // 사용자로부터 id 얻어오기
  },
};

export default resolvers;

